import React from 'react';
import DynamicFieldsForm from "../../../common/DynamicFieldsForm";
import {Row,Form, Col ,Radio, Input ,Divider} from "antd";
import SettingForm from "./SettingForm";
import DocumentPdf from "./DocumentPdf";

const { TextArea } = Input;
const style ={
  box_shadow:{
    webkitBoxShadow: "1px 3px 1px #9E9E9E",
    mozBoxShadow: "1px 3px 1px #9E9E9E",
    boxShadow: "1px 3px 1px #9E9E9E"
  }
}
class PrintPreview extends React.Component{
  constructor(props){
    super(props);
    this.state={
      pageSetting:'Page Setting',
      headerSetting:'Header Setting',
      patientSetting:'Patient Setting',
      footerSetting:'Footer Setting',


    }
  }

changeFormType=(e)=>{
  this.setState({
    selectedFormType : e.target.value
  })


}
  render(){


const WrappedSettingForm = Form.create({ name: 'setting' })(SettingForm);

    if(this.state.pageSetting === 'Page Setting') {
     return (<Row>
        <Col span={12}>
          <Radio.Group  buttonStyle="solid" size="small" value={this.state.selectedFormType} onChange={this.changeFormType}>
            <Radio.Button value={this.state.pageSetting}>{this.state.pageSetting}</Radio.Button>
            <Radio.Button value={this.state.headerSetting}>{this.state.headerSetting}</Radio.Button>
            <Radio.Button value={this.state.patientSetting}>{this.state.patientSetting}</Radio.Button>
            <Radio.Button value={this.state.footerSetting}>{this.state.footerSetting}</Radio.Button>
          </Radio.Group>
          <div className="div_padding_top">
            <h2>{this.state.pageSetting}</h2>
            <WrappedSettingForm/>
          </div>

        </Col>
      </Row>
      );
   }else {
      return (
        <p>hello</p>

      );


   }

  }

}
export default PrintPreview;
