import React from 'react';
import DynamicFieldsForm from "../../../common/DynamicFieldsForm";
import {Row,Form, Col ,Radio, Input ,Divider} from "antd";
import SettingForm from "./SettingForm";
import DocumentPdf from "./DocumentPdf";

const { TextArea } = Input;
const style ={
  box_shadow:{
    webkitBoxShadow: "1px 3px 1px #9E9E9E",
    mozBoxShadow: "1px 3px 1px #9E9E9E",
    boxShadow: "1px 3px 1px #9E9E9E"
  }
}
class PrintPreview extends React.Component{
  constructor(props){
    super(props);
    this.state={
      pageSetting:'Page Setting',
      headerSetting:'Header',
      patientSetting:'Patient',
      footerSetting:'Footer',


    }
  }

changeFormType=(e)=>{
  this.setState({
    selectedFormType : e.target.value
  })


}
  render(){


const WrappedSettingForm = Form.create({ name: 'setting' })(SettingForm);

    return ( <Row>
          <Col span={12}>
            <Radio.Group  buttonStyle="solid" size="small" value={this.state.selectedFormType} onChange={this.changeFormType}>
              <Radio.Button value={<WrappedSettingForm/>}>{this.state.pageSetting}</Radio.Button>
              <Radio.Button value={<WrappedSettingForm/>} >{this.state.headerSetting}</Radio.Button>
              <Radio.Button value={<WrappedSettingForm/>}>{this.state.patientSetting}</Radio.Button>
              <Radio.Button value="{<WrappedSettingForm/>}">{this.state.footerSetting}</Radio.Button>
            </Radio.Group>
            <div className="div_padding_top">
              {this.state.selectedFormType}

              <WrappedSettingForm/>
            </div>
          </Col>
          <Col span={12} >
          <div style={style.box_shadow}>
            <h3>pdf</h3>

            </div>
          </Col>


        </Row>);
  }

}
export default PrintPreview;
