import React from 'react';
import DynamicFieldsForm from "../../../common/DynamicFieldsForm";
import {Row,Form, Col ,Radio, Input ,Divider} from "antd";
const { TextArea } = Input;
const style ={
  box_shadow:{
    webkitBoxShadow: "1px 3px 1px #9E9E9E",
    mozBoxShadow: "1px 3px 1px #9E9E9E",
    boxShadow: "1px 3px 1px #9E9E9E"
  }
}
class PrintPreview extends React.Component{
  constructor(props){
    super(props);
  }
  // headerSetting(){
  //     alert("Im an alert");
  // }

  render(){
 const { getFieldDecorator } = this.props.form;
 const prefixSelector = getFieldDecorator('prefix', {
       initialValue: '86',
     })
    return ( <Row>
          <Col span={12}>
            <Radio.Group defaultValue="a" buttonStyle="solid" size="small">
              <Radio.Button value="a">Page Setting</Radio.Button>
              <Radio.Button value="b" onClick={this.headerSetting}>Header</Radio.Button>
              <Radio.Button value="c">Patient</Radio.Button>
              <Radio.Button value="d">Footer</Radio.Button>
            </Radio.Group>
            <div className="div_padding_top">
              <h2>Customize Header</h2>
              <Form>
              <Form.Item
                   label="Phone Number"
                 >
                   {getFieldDecorator('phone', {
                     rules: [{ required: true, message: 'Please input your phone number!' }],
                   })(
                     <Input addonBefore={prefixSelector} style={{ width: '100%' }} />
                   )}
                 </Form.Item>

                <h4>Include Header</h4>
                <Radio name="a">Yes</Radio>
                <Radio name="a" className="float_right">No I already have a letter head</Radio>
                <label> Header:
                   <Input />
                </label>
                <label>Left Text:
                   <Input />
                </label>
                <label> Right Text:
                   <Input />
                </label>

              </Form>
            </div>
          </Col>
          <Col span={12} >
          <div style={style.box_shadow}>
            <h3>pdf</h3>
            </div>
          </Col>


        </Row>);
  }
}
export default PrintPreview;
